mapper.select:
/* account_SQL.selectAccountList */ SELECT A.IDX, A.ACC_ID, A.SNS_TYPE, A.ACC_NAME, A.EMAIL, A.PHONE_NUM, A.USER_IMG, A.RIGHT, A.IS_DEL, A.NOTE, A.LAST_LOGIN_IP, DATE_FORMAT(A.JOIN_DT, '%Y-%m-%d %H:%i') AS JOIN_DT, DATE_FORMAT(A.LAST_LOGIN_DT, '%Y-%m-%d %H:%i') AS LAST_LOGIN_DT, DATE_FORMAT(A.MODIFY_DT, '%Y-%m-%d %H:%i') AS MODIFY_DT FROM ACCOUNT AS A WHERE 1=1
AND A.ACC_ID LIKE CONCAT('%',#{searchAccId},'%')
AND A.SNS_TYPE = #{searchSnsType}
AND A.ACC_NAME LIKE CONCAT('%',#{searchAccName},'%')
ORDER BY P.JOIN_DT ASC LIMIT #{startRownum}, #{cntPerPage}

/* account_SQL.selectAccountListTotalCnt */ SELECT COUNT(*) FROM ACCOUNT AS A WHERE 1=1
AND A.ACC_ID LIKE CONCAT('%',#{searchAccId},'%')
AND A.SNS_TYPE = #{searchSnsType}
AND A.ACC_NAME LIKE CONCAT('%',#{searchAccName},'%')

mapper.insert:

/* account_SQL.insertAccount */
INSERT INTO ACCOUNT
(
	ACC_ID, SNS_TYPE, ACC_NAME, EMAIL, PHONE_NUM,
	USER_IMG, RIGHT, NOTE
)
VALUES
(
	#{accId}, #{snsType}, #{accName}, #{email, jdbcType=VARCHAR}, #{phoneNum, jdbcType=VARCHAR},
	#{userImg, jdbcType=VARCHAR}, 'USER', #{note, jdbcType=VARCHAR}
)
